import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function Profile() {
  const [isEditMode, setIsEditMode] = useState(false);
  const [firstName, setFirstName] = useState('Mohammed');
  const [lastName, setLastName] = useState('bin Salman');
  const [username, setUsername] = useState('mohammed');
  const [email, setEmail] = useState('mohammed@example.com');
  const [phone, setPhone] = useState('+966501234567');
  const [address, setAddress] = useState('123 King Fahd Road, Riyadh, Saudi Arabia');

  const [paymentMethods, setPaymentMethods] = useState([
    {
      type: 'Visa',
      last4: '5432',
      expires: '05/26',
      isDefault: true
    },
    {
      type: 'Mastercard',
      last4: '7890',
      expires: '08/27',
      isDefault: false
    }
  ]);

  const [showPaymentModal, setShowPaymentModal] = useState(false);
  const [newPayType, setNewPayType] = useState('');
  const [newPayLast4, setNewPayLast4] = useState('');
  const [newPayExpires, setNewPayExpires] = useState('');

  const handleEdit = () => {
    setIsEditMode(true);
  };

  const handleSave = () => {
    setIsEditMode(false);
  };

  const handleCancel = () => {
    setIsEditMode(false);
  };

  const handleAddPayment = () => {
    if (newPayType.trim() && newPayLast4.trim() && newPayExpires.trim()) {
      const newMethod = {
        type: newPayType,
        last4: newPayLast4,
        expires: newPayExpires,
        isDefault: false
      };
      setPaymentMethods([...paymentMethods, newMethod]);
    }
    setNewPayType('');
    setNewPayLast4('');
    setNewPayExpires('');
    setShowPaymentModal(false);
  };

  const handleDeletePayment = (index) => {
    const updated = [...paymentMethods];
    updated.splice(index, 1);
    setPaymentMethods(updated);
  };

  const handleSetDefault = (index) => {
    const updated = paymentMethods.map((pm, i) => {
      return { ...pm, isDefault: i === index };
    });
    setPaymentMethods(updated);
  };

  const handlePhoneChange = (e) => {
    const val = e.target.value;
    if (/^\+?\d*$/.test(val)) {
      setPhone(val);
    }
  };

  return (
    <div className="bg-light">
      <div className="container-fluid p-0">
        <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
          <div className="container">
            <a className="navbar-brand" href="/home">KADAD+</a>
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav ms-auto">
                <li className="nav-item">
                  <a className="nav-link" href="/home">Home</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link active" href="/profile">Profile</a>
                </li>
                <li className="nav-item">
                  <a className="nav-link" href="/">Logout</a>
                </li>
              </ul>
            </div>
          </div>
        </nav>

        <div className="container py-4">
          <div className="row">
            <div className="col-lg-4 mb-4">
              <div className="card shadow-sm">
                <div className="card-body text-center">
                  <div className="mb-3">
                    <img
                      src="https://via.placeholder.com/150"
                      className="rounded-circle img-thumbnail"
                      alt="Profile"
                    />
                  </div>
                  <h4 className="mb-1">{firstName} {lastName}</h4>
                  <p className="text-muted mb-3">@{username}</p>
                  <div className="d-grid">
                    {!isEditMode && (
                      <button className="btn btn-outline-primary" onClick={handleEdit}>
                        <i className="fas fa-edit me-1"></i> Edit Profile
                      </button>
                    )}
                    {isEditMode && (
                      <div className="d-flex gap-2 flex-wrap justify-content-center">
                        <button className="btn btn-primary" onClick={handleSave}>
                          Save Changes
                        </button>
                        <button className="btn btn-outline-secondary" onClick={handleCancel}>
                          Cancel
                        </button>
                      </div>
                    )}
                  </div>
                </div>
                <div className="card-footer bg-white">
                  <div className="row text-center">
                    <div className="col-4">
                      <h5>12</h5>
                      <small className="text-muted">Rides</small>
                    </div>
                    <div className="col-4">
                      <h5>4.8</h5>
                      <small className="text-muted">Rating</small>
                    </div>
                    <div className="col-4">
                      <h5>$250</h5>
                      <small className="text-muted">Spent</small>
                    </div>
                  </div>
                </div>
              </div>
            </div>

            <div className="col-lg-8">
              <div className="card shadow-sm mb-4">
                <div className="card-header bg-white">
                  <h5 className="mb-0">Account Details</h5>
                </div>
                <div className="card-body">
                  <form>
                    <div className="row mb-3">
                      <div className="col-md-6">
                        <label htmlFor="firstName" className="form-label">First Name</label>
                        <input
                          type="text"
                          className="form-control"
                          id="firstName"
                          disabled={!isEditMode}
                          value={firstName}
                          onChange={(e) => setFirstName(e.target.value)}
                        />
                      </div>
                      <div className="col-md-6">
                        <label htmlFor="lastName" className="form-label">Last Name</label>
                        <input
                          type="text"
                          className="form-control"
                          id="lastName"
                          disabled={!isEditMode}
                          value={lastName}
                          onChange={(e) => setLastName(e.target.value)}
                        />
                      </div>
                    </div>
                    <div className="mb-3">
                      <label htmlFor="username" className="form-label">Username</label>
                      <input
                        type="text"
                        className="form-control"
                        id="username"
                        disabled={!isEditMode}
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                      />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="email" className="form-label">Email</label>
                      <input
                        type="email"
                        className="form-control"
                        id="email"
                        disabled={!isEditMode}
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                      />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="phone" className="form-label">Phone</label>
                      <input
                        type="tel"
                        className="form-control"
                        id="phone"
                        disabled={!isEditMode}
                        value={phone}
                        onChange={handlePhoneChange}
                      />
                    </div>
                    <div className="mb-3">
                      <label htmlFor="address" className="form-label">Address</label>
                      <textarea
                        className="form-control"
                        id="address"
                        rows="2"
                        disabled={!isEditMode}
                        value={address}
                        onChange={(e) => setAddress(e.target.value)}
                      ></textarea>
                    </div>
                  </form>
                </div>
              </div>

              <div className="card shadow-sm mb-4">
                <div className="card-header bg-white d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">Payment Methods</h5>
                  <button
                    className="btn btn-sm btn-outline-primary"
                    onClick={() => setShowPaymentModal(true)}
                  >
                    <i className="fas fa-plus me-1"></i> Add New
                  </button>
                </div>
                <div className="card-body">
                  {paymentMethods.map((pm, index) => (
                    <div
                      key={index}
                      className="d-flex justify-content-between align-items-center mb-3 p-2 border rounded"
                    >
                      <div className="d-flex align-items-center">
                        <i
                          className={`fab fa-cc-${
                            pm.type.toLowerCase().includes('visa')
                              ? 'visa'
                              : pm.type.toLowerCase().includes('master')
                              ? 'mastercard'
                              : 'paypal'
                          } fa-2x text-primary me-3`}
                        ></i>
                        <div>
                          <p className="mb-0 fw-bold">
                            {pm.type} ending in {pm.last4}
                          </p>
                          <p className="mb-0 small text-muted">Expires {pm.expires}</p>
                        </div>
                      </div>
                      <div>
                        {pm.isDefault && <span className="badge bg-success me-2">Default</span>}
                        {!pm.isDefault && (
                          <button
                            className="btn btn-sm btn-outline-secondary me-2"
                            onClick={() => {
                              const updated = paymentMethods.map((m, i) => ({
                                ...m,
                                isDefault: i === index,
                              }));
                              setPaymentMethods(updated);
                            }}
                          >
                            Set Default
                          </button>
                        )}
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => {
                            const updated = [...paymentMethods];
                            updated.splice(index, 1);
                            setPaymentMethods(updated);
                          }}
                        >
                          <i className="fas fa-trash"></i>
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              <div className="card shadow-sm">
                <div className="card-header bg-white">
                  <h5 className="mb-0">Recent Rides</h5>
                </div>
                <div className="card-body">
                  <div className="table-responsive">
                    <table className="table table-hover">
                      <thead>
                        <tr>
                          <th>Date</th>
                          <th>From</th>
                          <th>To</th>
                          <th>Amount</th>
                          <th>Status</th>
                        </tr>
                      </thead>
                      <tbody>
                        <tr>
                          <td>Apr 10, 2025</td>
                          <td>Downtown</td>
                          <td>Airport</td>
                          <td>$18.00</td>
                          <td><span className="badge bg-success">Completed</span></td>
                        </tr>
                        <tr>
                          <td>Apr 5, 2025</td>
                          <td>Mall</td>
                          <td>University</td>
                          <td>$12.50</td>
                          <td><span className="badge bg-success">Completed</span></td>
                        </tr>
                        <tr>
                          <td>Mar 28, 2025</td>
                          <td>Hospital</td>
                          <td>Home</td>
                          <td>$15.75</td>
                          <td><span className="badge bg-success">Completed</span></td>
                        </tr>
                      </tbody>
                    </table>
                  </div>
                  <div className="text-center mt-2">
                    <a href="/my-rides" className="text-decoration-none">View My Rides</a>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>


      {showPaymentModal && (
        <div
          className="modal fade show"
          style={{ display: 'block', backgroundColor: 'rgba(0,0,0,0.5)' }}
          tabIndex="-1"
        >
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Add New Payment Method</h5>
                <button
                  type="button"
                  className="btn-close"
                  onClick={() => setShowPaymentModal(false)}
                ></button>
              </div>
              <div className="modal-body">
                <div className="mb-3">
                  <label className="form-label">Type (e.g., Visa)</label>
                  <input
                    type="text"
                    className="form-control"
                    value={newPayType}
                    onChange={(e) => {
                      setNewPayType(e.target.value);
                    }}
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Last 4 Digits</label>
                  <input
                    type="text"
                    className="form-control"
                    maxLength={4}
                    value={newPayLast4}
                    onChange={(e) => {
                      if (/^\d*$/.test(e.target.value)) {
                        setNewPayLast4(e.target.value);
                      }
                    }}
                  />
                </div>
                <div className="mb-3">
                  <label className="form-label">Expires (MM/YY)</label>
                  <input
                    type="text"
                    className="form-control"
                    value={newPayExpires}
                    onChange={(e) => setNewPayExpires(e.target.value)}
                  />
                </div>
              </div>
              <div className="modal-footer">
                <button
                  type="button"
                  className="btn btn-secondary"
                  onClick={() => setShowPaymentModal(false)}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="btn btn-primary"
                  onClick={handleAddPayment}
                >
                  Add
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}

export default Profile;
